import './App.css';
import React, { useState } from 'react';
import CustomizedTables from './element/CustomizedTables';
import GetNextWeekMenu from './element/GetNextWeekMenu';
import CheckboxLabels from './element/CheckboxLabels';
import PinnedSubheaderList from './element/PinnedSubheaderList';
import AddDish from './element/AddDish';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

function App() {
  // State to store data returned by GetNextWeekMenu
  const [menu, setMenu] = useState(null);
  const [ingredientsList, setIngredientsList] = useState(null);

  // Callback function to handle data returned by GetNextWeekMenu
  const handleMenuData = (menuData, ingredientsData) => {
    setMenu(menuData);
    setIngredientsList(ingredientsData);
  };

  return (
    <div className="App">
      <header className="App-header"></header>
      <div style={styles.parentContainer}>
        <Box style={styles.container}>
          <GetNextWeekMenu style={styles.container} onMenuFetched={handleMenuData} />
        </Box>
        <Box style={styles.container}>
          <CustomizedTables style={styles.container} />
        </Box>
        <Box style={styles.container}>
          <div style={styles.horizontalSplitContainer}>
            <Box style={styles.horizontalContainer}>
              <Typography>Do you have this in your refrigerator?</Typography>
              <CheckboxLabels />
            </Box>
            <Box style={styles.horizontalContainer}>
              <Typography>Buy!</Typography>
              <PinnedSubheaderList />
            </Box>
          </div>
        </Box>
        <Box style={styles.container}>
          <Typography>Upload New Dish</Typography>
          <AddDish />
        </Box>
      </div>
      {/* Optionally render the fetched menu data and ingredients list here */}
      {menu && (
        <div>
          <Typography variant="h6">Next Week's Menu:</Typography>
          <Typography>{menu}</Typography>
          <Typography variant="h6">Ingredients:</Typography>
          <Typography>{ingredientsList}</Typography>
        </div>
      )}
    </div>
  );
}

const styles = {
  parentContainer: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
    alignItems: 'center',
    height: '100vh',
    paddingTop: '1vh',
    gap: '20px',
  },
  container: {
    width: '60%',
    textAlign: 'center',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '8px',
  },
  horizontalSplitContainer: {
    display: 'flex',
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginTop: '20px',
  },
  horizontalContainer: {
    flex: 1,
    border: '1px solid #ddd',
    padding: '10px',
    borderRadius: '4px',
    margin: '0 10px',
  },
};

export default App;
