import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useState } from 'react';

export default function GetNextWeekMenu({ onMenuFetched }) {
  const [error, setError] = useState(null); // State to store errors
  const [loading, setLoading] = useState(false); // Loading state

  // Function to fetch menu when button is clicked
  const fetchMenu = async () => {
    setLoading(true); // Set loading to true when fetching begins
    setError(null); // Clear any previous errors
    // onMenuFetched(null); // Clear the old menu data
    try {
      const response = await fetch('https://htjdonjs8c.execute-api.us-east-2.amazonaws.com/test/', {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      
      if (!response.ok) {
        throw new Error(`Error: ${response.status}`);
      }
      // onMenuFetched(null); // Clear the old menu data
      const data = await response.json();
          // Step 1: Group by `dishName`
      const groupedByDish = parsedData.reduce((acc, item) => {
        if (!acc[item.dishName]) acc[item.dishName] = [];
        acc[item.dishName].push(item);
        return acc;
      }, {});

      // Step 2: Shuffle the dish groups
      const shuffledDishes = Object.values(groupedByDish).sort(() => 0.5 - Math.random());

      // Step 3: Flatten the shuffled groups back into a single array
      const randomizedData = shuffledDishes.flat();
      // const randomizedData = data.body.sort(() => 0.5 - Math.random()); // Shuffle the data
      onMenuFetched(randomizedData); // Send shuffled data to the App component
    } catch (err) {
      console.error('Error fetching menu:', err);
      setError('Failed to fetch the menu. Please try again.');
    } finally {
      setLoading(false); // Set loading to false after fetching completes
    }
  };

  return (
    <div>
      <Box sx={{ textAlign: 'center' }}>
        <Typography variant="h3" gutterBottom style={{ marginBottom: '10px' }}>
          Click Button to Generate Menu of Next Week
        </Typography>
        <Button variant="contained" onClick={fetchMenu} disabled={loading}>
          {loading ? 'Loading...' : 'GENERATE'}
        </Button>

        {loading && <Typography>Loading...</Typography>}

        {error && (
          <Typography style={{ color: 'red' }}>
            {error}
          </Typography>
        )}
      </Box>
    </div>
  );
}
