import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useState } from 'react';

export default function GetNextWeekMenu() {
  const [menu, setMenu] = useState(null); // State to store the parsed menu
  const [ingredientsList, setIngredientsList] = useState(null); // State to store the list of unique ingredients
  const [error, setError] = useState(null); // State to store errors
  const [loading, setLoading] = useState(false); // Loading state

  // Function to fetch menu when button is clicked
  const fetchMenu = async () => {
    setLoading(true); // Set loading to true when fetching begins
    setError(null); // Clear any previous errors
    try {
      const response = await fetch('https://htjdonjs8c.execute-api.us-east-2.amazonaws.com/test/', {
        method: 'GET', // or 'POST', depending on your API
        headers: {
          'Content-Type': 'application/json', // Specify the content type
          // Add any additional headers here, such as authorization tokens if needed
          // 'Authorization': 'Bearer your-token-here'
        },
      });
      
      if (!response.ok) {
        throw new Error(`Error: ${response.status}`);
      }
      
      const data = await response.json();
      
      // Parse the menu data after fetching
      const { parsedMenu, uniqueIngredients } = parseMenu(data.body);
      
      setMenu(parsedMenu); // Set the parsed menu data (grouped by class)
      setIngredientsList(uniqueIngredients.join(', ')); // Set the unique ingredients as a comma-separated list
    } catch (err) {
      console.error('Error fetching menu:', err);
      setError('Failed to fetch the menu. Please try again.');
    } finally {
      setLoading(false); // Set loading to false after fetching completes
    }
  };

  // Function to parse the menu data and group dish names by class, removing duplicates
  const parseMenu = (menuData) => {
    const dishes = JSON.parse(menuData);

    // Object to store dish names grouped by class (key: className, value: Set of dish names)
    const groupedDishes = {};

    // Set to store unique ingredients
    const uniqueIngredients = new Set();

    // Loop through the dishes and group dish names by class, while also collecting ingredients
    dishes.forEach((dish) => {
      const { className, dishName, ingredientName } = dish;

      // Add the dish name to the corresponding class group
      if (!groupedDishes[className]) {
        groupedDishes[className] = new Set();
      }
      groupedDishes[className].add(dishName);

      // Add the ingredient to the unique ingredients set
      uniqueIngredients.add(ingredientName);
    });

    // Format the grouped dish names by class as a string
    const formattedMenu = Object.entries(groupedDishes).map(([className, dishes]) => {
      return `${className}: ${[...dishes].join(', ')}`;
    }).join('; ');

    // Return both the formatted menu and the list of unique ingredients
    return { parsedMenu: formattedMenu, uniqueIngredients: [...uniqueIngredients] };
  };

  return (
    <div>
      <Box sx={{ textAlign: 'center' }}>
        <Typography variant="h3" gutterBottom style={{ marginBottom: '10px' }}>
          Click Button to Generate Menu of Next Week
        </Typography>
        <Button variant="contained" style={styles.button} onClick={fetchMenu} disabled={loading}>
          {loading ? 'Loading...' : 'GENERATE'}
        </Button>

        {/* Display loading indicator */}
        {loading && <Typography variant="body1">Loading...</Typography>}

        {/* Display the fetched and parsed menu (grouped by class) */}
        {menu && (
          <Typography variant="body1" style={{ marginTop: '20px' }}>
            Next week's menu (by class): {menu}
          </Typography>
        )}

        {/* Display the unique ingredients list */}
        {ingredientsList && (
          <Typography variant="body1" style={{ marginTop: '20px' }}>
            Unique ingredients: {ingredientsList}
          </Typography>
        )}

        {/* Display any error */}
        {error && (
          <Typography variant="body1" style={{ marginTop: '20px', color: 'red' }}>
            {error}
          </Typography>
        )}
      </Box>
    </div>
  );
}

const styles = {
  button: {
    width: '200px', // Button width
    height: '60px', // Button height
    fontSize: '18px', // Text size
  },
};
