import './App.css';
import React from 'react';
import ButtonUsage from './element/ButtonUsage';
import CustomizedTables from './element/CustomizedTables';
import CheckboxLabels from './element/CheckboxLabels';
import PinnedSubheaderList from './element/PinnedSubheaderList';
import AddDish from './element/AddDish';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

// import 'element-theme-default'; 

function App() {
  return (

    <div className="App">
      <header className="App-header">

      </header>
      <div style={styles.parentContainer}>
        <Box style={styles.container}>
          <ButtonUsage style={styles.container}/>
        </Box>
        <Box style={styles.container}>
          <CustomizedTables style={styles.container}/>
        </Box>
        <Box style={styles.container}>
          {/* Nested Horizontal Flexbox for splitting */}
          <div style={styles.horizontalSplitContainer}>
            <Box style={styles.horizontalContainer}>
              <Typography>Do you have this in your refrigerator?</Typography>
              <CheckboxLabels />
            </Box>
            <Box style={styles.horizontalContainer}>
              <Typography>Buy!</Typography>
              <PinnedSubheaderList />
            </Box>
          </div>
        </Box>
        <Box style={styles.container}>
          <Typography>Upload New Dish</Typography>
          <AddDish />
        </Box>
      </div>
    </div>
  );
}

const styles = {
  parentContainer: {
    display: 'flex',
    flexDirection: 'column',          // Stack the containers vertically
    justifyContent: 'flex-start',     // Align containers towards the top
    alignItems: 'center',             // Center the containers horizontally
    height: '100vh',                  // Full viewport height
    paddingTop: '1vh',               // Adjust this to move containers further up or down
    gap: '20px',                      // Add space between the containers
  },
  container: {
    width: '60%',                     // Set container width (can be adjusted)
    textAlign: 'center',              // Center content within each container
    padding: '20px',                  // Add padding
    border: '1px solid #ccc',         // Optional: Border for visual clarity
    borderRadius: '8px',              // Optional: Rounded corners
  },
  horizontalSplitContainer: {
    display: 'flex',                  // Flexbox layout for horizontal split
    flexDirection: 'row',             // Horizontal layout
    justifyContent: 'space-between',  // Space between the two containers
    marginTop: '20px',                // Add space between text and split containers
  },
  horizontalContainer: {
    flex: 1,                          // Make each sub-container take equal space
    border: '1px solid #ddd',         // Optional: Border for sub-containers
    padding: '10px',                  // Padding inside each sub-container
    borderRadius: '4px',              // Rounded corners
    margin: '0 10px',                 // Add horizontal margin between the two containers
  },
}

export default App;
