import './App.css';
import React from 'react';
import ButtonUsage from './element/ButtonUsage';
import CustomizedTables from './element/CustomizedTables';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
// import 'element-theme-default'; 

function App() {
  return (
    <div className="App">
      <header className="App-header">

        <CustomizedTables style={styles.container}/>
      </header>
        <div style={styles.parentContainer}>
        {/* First Container */}
        <Box style={styles.container}>
          <ButtonUsage style={styles.container}/>
        </Box>

        {/* Second Container */}
        <Box style={styles.container}>

        </Box>

        {/* Third Container with Vertical Split */}
        <Box style={styles.container}>
          <Typography variant="h4" gutterBottom>
            Container 3
          </Typography>

          {/* Nested Vertical Flexbox for splitting */}
          <div style={styles.verticalSplitContainer}>
            <Box style={styles.verticalContainer}>
              <Typography>Upper Container</Typography>
            </Box>
            <Box style={styles.verticalContainer}>
              <Typography>Lower Container</Typography>
            </Box>
          </div>
        </Box>
      </div>
    </div>
  );
}

const styles = {
  parentContainer: {
    display: 'flex',
    justifyContent: 'space-around',  // Distribute containers evenly
    alignItems: 'center',            // Align containers vertically in the center
    height: '100vh',                 // Full viewport height
  },
  container: {
    width: '30%',                    // Each main container takes 30% of the width
    textAlign: 'center',             // Center content within each container
    padding: '20px',                 // Add some padding
    border: '1px solid #ccc',        // Optional: Add borders for clarity
    borderRadius: '8px',             // Optional: Rounded corners
  },
  verticalSplitContainer: {
    display: 'flex',
    flexDirection: 'column',         // Vertical layout for the two sub-containers
    height: '200px',                 // Set height for the vertical containers
    justifyContent: 'space-between', // Space between the two containers
  },
  verticalContainer: {
    border: '1px solid #ddd',        // Optional: Add borders for the inner containers
    padding: '10px',                 // Padding inside each sub-container
    borderRadius: '4px',             // Optional: Rounded corners for inner containers
    flex: 1,                         // Make each vertical container take equal space
  },
}

export default App;
