import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { useState } from 'react';

export default function getNextWeekMenu() {
  const [menu, setMenu] = useState(null); // State to store the menu
  const [error, setError] = useState(null); // State to store errors

  // Function to fetch menu when button is clicked
  const fetchMenu = async () => {
    try {
      const response = await fetch('https://htjdonjs8c.execute-api.us-east-2.amazonaws.com/test'); // Replace with your actual API Gateway endpoint
      const data = await response.json();
      setMenu(data); // Set the fetched menu
      setError(null); // Clear any previous errors
    } catch (err) {
      console.error('Error fetching menu:', err);
      setError('Failed to fetch the menu. Please try again.');
    }
  };

  return (
    <div>
      <Box sx={{ textAlign: 'center' }}> {/* Aligns text and button to the center */}
        <Typography variant="h3" gutterBottom style={{ marginBottom: '10px' }}>
          Click Button to Generate Menu of Next Week
        </Typography>
        <Button variant="contained" style={styles.button} onClick={fetchMenu}> {/* Added onClick event */}
          GENERATE
        </Button>

        {/* Display the fetched menu or error */}
        {menu && (
          <Typography variant="body1" style={{ marginTop: '20px' }}>
            Next week's menu: {JSON.stringify(menu, null, 2)} {/* Displaying the menu data */}
          </Typography>
        )}
        {error && (
          <Typography variant="body1" style={{ marginTop: '20px', color: 'red' }}>
            {error}
          </Typography>
        )}
      </Box>
    </div>
  );
}

const styles = {
  button: {
    width: '200px', // Button width
    height: '60px', // Button height
    fontSize: '18px', // Text size
  },
};
