// import * as React, { useState } from 'react';
import { styled } from '@mui/material/styles';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell, { tableCellClasses } from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import Button from '@mui/material/Button';
const React = require('react');
const { useState } = React;
const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));

const daysOfWeek = ["MON", "TUE", "WED", "THU", "FRI", "SAT", "SUN"];

export default function CustomizedTables({ parsedMenu }) {
  if (!parsedMenu) {
    return <div>No menu data available</div>;
  }

  // Parse the string `parsedMenu` into a usable object format
  const menuData = {};

  parsedMenu.split('; ').forEach(entry => {
    const [className, dishes] = entry.split(': ');
    menuData[className] = dishes.split(', ');
  });

  // State to store the current menu displayed in the table
  const [currentMenu, setCurrentMenu] = useState(menuData);

  // Function to handle changing a dish
  const handleChangeDish = (className, day) => {
    const currentDishes = currentMenu[className];
    const currentDishIndex = currentDishes.indexOf(currentMenu[className][day]);
    
    // Get the next dish from the list, wrapping around if at the end
    const nextDishIndex = (currentDishIndex + 1) % currentDishes.length;
    const newMenu = {
      ...currentMenu,
      [className]: {
        ...currentMenu[className],
        [day]: currentDishes[nextDishIndex],
      },
    };

    setCurrentMenu(newMenu); // Update the menu with the new dish
  };

  return (
    <TableContainer component={Paper}>
      <Table sx={{ minWidth: 700 }} aria-label="customized table">
        <TableHead>
          <TableRow>
            <StyledTableCell>Category</StyledTableCell>
            {daysOfWeek.map(day => (
              <StyledTableCell key={day} align="center">{day}</StyledTableCell>
            ))}
          </TableRow>
        </TableHead>
        <TableBody>
          {Object.entries(currentMenu).map(([className, dishes]) => (
            <StyledTableRow key={className}>
              <StyledTableCell component="th" scope="row">
                {className}
              </StyledTableCell>
              {daysOfWeek.map((day, index) => (
                <StyledTableCell key={day} align="center">
                  {dishes[index] || "-"} {/* Display the dish or a dash if no dish */}
                  {dishes[index] && (
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={() => handleChangeDish(className, index)} // Handle changing the dish
                      style={{ marginLeft: '10px' }}
                    >
                      Change
                    </Button>
                  )}
                </StyledTableCell>
              ))}
            </StyledTableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
